static void Main(string[] args)
{
    string numberAsString = "128";
    int parsedValue;
    bool success = int.TryParse(numberAsString, out parsedValue);
    //The returned boolean is true when parsing was successful

    if (success)
        Console.WriteLine("Parsing successful - number is {0}", parsedValue);
    else
        Console.WriteLine("Parsing failed");


    Console.ReadLine();
}

static void Main(string[] args)
        {
            bool isAdmin = false;
            bool isRegistered = true;
            string userName = "";
            userName = Console.ReadLine();
            if(isRegistered)
            {
                Console.WriteLine("Hi there, registered user");
                if (userName != "")
                {
                    Console.WriteLine("Hi there, {0}", userName);
                    if(userName.Equals("Admin"))
                    {
                        Console.WriteLine("Hi there, Admin!");
                    }
                }
            }

            if(isAdmin || isRegistered)
            {
                Console.WriteLine("You are logged in");
            }
            Console.ReadLine();
        }
