int[] grades;

//datatype[] arrayName = new dataType[amountOfEntries]
int[] grades = new int[5];

//Another way od initializing
int[] grades = {32, 31, 324, 12, 23};

//Third way od initializing
int[] grades = new int[] {32, 45, 12, 32};
//arrayName[index] = value;
grades[0] = 2;
grades[1] = 3;

//declare jagged array
int[][] jaggedArray = new int[3][];
jaggedArray[0] = new int[5];
jaggedArray[1] = new int[3];
jaggedArray[2] = new int[8];
//initzialiazing of jagged array
//first way
jaggedArray[0] = new int[] { 2, 3, 4, 5, 7};
jaggedArray[1] = new int[] { 1, 2, 1 };
jaggedArray[2] = new int[] { 1, 2, 3, 4, 5, 6, 7, 8 };

//anothert way
int[][] jaggedArray2 = new int[][]
{
    new int[] { 2, 3, 4, 5, 7},
    new int[] { 1, 2, 1 },
    new int[] { 1, 2, 3, 4, 5, 6, 7, 8 }
};

//writing all jagged array elements
for (int i = 0; i < jaggedArray2.Length; i++)
{
    Console.WriteLine("Element {0}", i);
    for (int j = 0; j < jaggedArray2[i].Length; j++)
    {
        Console.WriteLine(jaggedArray2[i][j]);
    }
}
